const testLocation = new netbox.Location("testLocation", {siteId: testSite.id});
const testPowerPanel = new netbox.PowerPanel("testPowerPanel", {
    siteId: testSite.id,
    locationId: testLocation.id,
});
const testPowerFeed = new netbox.PowerFeed("testPowerFeed", {
    powerPanelId: testPowerPanel.id,
    status: "active",
    type: "primary",
    supply: "ac",
    phase: "single-phase",
    voltage: 250,
    amperage: 100,
    maxPercentUtilization: 80,
});
